@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 250 20% 6%;
    --foreground: 0 0% 98%;
    --card: 250 15% 10%;
    --card-foreground: 0 0% 98%;
    --popover: 250 15% 10%;
    --popover-foreground: 0 0% 98%;
    --primary: 280 80% 65%;
    --primary-foreground: 0 0% 100%;
    --secondary: 320 70% 60%;
    --secondary-foreground: 0 0% 100%;
    --muted: 250 15% 15%;
    --muted-foreground: 250 10% 60%;
    --accent: 280 60% 50%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 98%;
    --border: 250 15% 20%;
    --input: 250 15% 15%;
    --ring: 280 80% 65%;
    --radius: 0.75rem;

    /* Custom gradients */
    --gradient-primary: linear-gradient(135deg, hsl(280 80% 65%), hsl(320 70% 60%));
    --gradient-secondary: linear-gradient(135deg, hsl(320 70% 60%), hsl(350 80% 65%));
    --gradient-glass: linear-gradient(135deg, hsl(250 15% 15% / 0.8), hsl(250 15% 10% / 0.6));
    --gradient-shine: linear-gradient(135deg, hsl(280 80% 70% / 0.2), transparent);

    /* Shadows */
    --shadow-glow: 0 0 40px hsl(280 80% 65% / 0.3);
    --shadow-card: 0 8px 32px hsl(0 0% 0% / 0.3);
    --shadow-hover: 0 12px 40px hsl(280 80% 65% / 0.25);

    /* Animations */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: all 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
  }
}

@layer components {
  .glass-card {
    @apply bg-card/60 backdrop-blur-xl border border-border/50 rounded-2xl;
    box-shadow: var(--shadow-card);
  }

  .gradient-text {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-primary);
  }

  .gradient-border {
    @apply relative;
  }

  .gradient-border::before {
    content: '';
    @apply absolute inset-0 rounded-2xl p-[1px];
    background: var(--gradient-primary);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  .btn-gradient {
    background: var(--gradient-primary);
    @apply text-primary-foreground font-semibold px-6 py-3 rounded-xl;
    box-shadow: var(--shadow-glow);
    transition: var(--transition-smooth);
  }

  .btn-gradient:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-hover);
  }

  .upload-zone {
    @apply border-2 border-dashed border-border/50 rounded-2xl;
    @apply bg-muted/30 backdrop-blur-sm;
    @apply transition-all duration-300;
  }

  .upload-zone:hover {
    @apply border-primary/50 bg-muted/50;
  }

  .clothing-card {
    @apply glass-card overflow-hidden cursor-pointer;
    transition: var(--transition-bounce);
  }

  .clothing-card:hover {
    transform: translateY(-4px) scale(1.02);
    box-shadow: var(--shadow-hover);
  }

  .clothing-card.selected {
    @apply ring-2 ring-primary ring-offset-2 ring-offset-background;
  }

  .shimmer {
    background: linear-gradient(
      90deg,
      hsl(var(--muted)) 0%,
      hsl(var(--muted-foreground) / 0.1) 50%,
      hsl(var(--muted)) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  @keyframes shimmer {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }

  @keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 20px hsl(280 80% 65% / 0.3); }
    50% { box-shadow: 0 0 40px hsl(280 80% 65% / 0.6); }
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  .float {
    animation: float 3s ease-in-out infinite;
  }
}

