import { useCallback, useState } from 'react';
import { Upload, X, User } from 'lucide-react';

interface UploadZoneProps {
  image: string | null;
  onImageChange: (image: string | null) => void;
}

export function UploadZone({ image, onImageChange }: UploadZoneProps) {
  const [isDragging, setIsDragging] = useState(false);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onImageChange(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  }, [onImageChange]);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onImageChange(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  }, [onImageChange]);

  const handleClear = useCallback(() => {
    onImageChange(null);
  }, [onImageChange]);

  if (image) {
    return (
      <div className="relative w-full aspect-[3/4] rounded-2xl overflow-hidden glass-card group">
        <img
          src={image}
          alt="Uploaded"
          className="w-full h-full object-cover"
        />
        <button
          onClick={handleClear}
          className="absolute top-3 right-3 p-2 rounded-full bg-background/80 backdrop-blur-sm
                     text-foreground opacity-0 group-hover:opacity-100 transition-opacity
                     hover:bg-destructive hover:text-destructive-foreground"
        >
          <X className="w-4 h-4" />
        </button>
        <div className="absolute bottom-0 left-0 right-0 p-3 bg-gradient-to-t from-background/90 to-transparent">
          <p className="text-sm text-muted-foreground">点击右上角可更换照片</p>
        </div>
      </div>
    );
  }

  return (
    <div
      className={`upload-zone w-full aspect-[3/4] flex flex-col items-center justify-center gap-4 cursor-pointer
                  ${isDragging ? 'border-primary bg-primary/10' : ''}`}
      onDragOver={(e) => { e.preventDefault(); setIsDragging(true); }}
      onDragLeave={() => setIsDragging(false)}
      onDrop={handleDrop}
      onClick={() => document.getElementById('file-input')?.click()}
    >
      <input
        id="file-input"
        type="file"
        accept="image/*"
        className="hidden"
        onChange={handleFileInput}
      />
      <div className="w-20 h-20 rounded-full bg-muted flex items-center justify-center">
        <User className="w-10 h-10 text-muted-foreground" />
      </div>
      <div className="flex flex-col items-center gap-2">
        <div className="flex items-center gap-2 text-foreground">
          <Upload className="w-5 h-5" />
          <span className="font-medium">上传人物照片</span>
        </div>
        <p className="text-sm text-muted-foreground text-center px-4">
          拖放或点击上传<br />支持 JPG, PNG 格式
        </p>
      </div>
    </div>
  );
}

import { Check } from 'lucide-react';

interface ClothingCardProps {
  id: string;
  image: string;
  name: string;
  selected: boolean;
  onSelect: (id: string) => void;
}

export function ClothingCard({ id, image, name, selected, onSelect }: ClothingCardProps) {
  return (
    <div
      className={`clothing-card p-2 ${selected ? 'selected' : ''}`}
      onClick={() => onSelect(id)}
    >
      <div className="relative aspect-[3/4] rounded-xl overflow-hidden bg-muted">
        <img
          src={image}
          alt={name}
          className="w-full h-full object-cover"
        />
        {selected && (
          <div className="absolute top-2 right-2 w-6 h-6 rounded-full bg-primary flex items-center justify-center">
            <Check className="w-4 h-4 text-primary-foreground" />
          </div>
        )}
      </div>
      <p className="mt-2 text-sm text-foreground text-center truncate px-1">{name}</p>
    </div>
  );
}

import { Sparkles, Download, RefreshCw } from 'lucide-react';

interface ResultPreviewProps {
  result: string | null;
  isLoading: boolean;
  onDownload: () => void;
  onRetry: () => void;
}

export function ResultPreview({ result, isLoading, onDownload, onRetry }: ResultPreviewProps) {
  if (isLoading) {
    return (
      <div className="w-full aspect-[3/4] glass-card rounded-2xl flex flex-col items-center justify-center gap-4">
        <div className="relative">
          <div className="w-16 h-16 rounded-full border-4 border-muted border-t-primary spin-slow" />
          <Sparkles className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-6 h-6 text-primary" />
        </div>
        <div className="text-center">
          <p className="text-foreground font-medium">AI 正在换装中...</p>
          <p className="text-sm text-muted-foreground mt-1">请稍候，这可能需要几秒钟</p>
        </div>
      </div>
    );
  }

  if (result) {
    return (
      <div className="relative w-full aspect-[3/4] rounded-2xl overflow-hidden glass-card group">
        <img
          src={result}
          alt="AI Result"
          className="w-full h-full object-cover"
        />
        <div className="absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-background/90 to-transparent
                        opacity-0 group-hover:opacity-100 transition-opacity">
          <div className="flex gap-2">
            <button
              onClick={onDownload}
              className="flex-1 flex items-center justify-center gap-2 py-2 rounded-lg
                         bg-primary/20 backdrop-blur-sm text-primary-foreground
                         hover:bg-primary/30 transition-colors"
            >
              <Download className="w-4 h-4" />
              <span className="text-sm">下载</span>
            </button>
            <button
              onClick={onRetry}
              className="flex-1 flex items-center justify-center gap-2 py-2 rounded-lg
                         bg-muted/50 backdrop-blur-sm text-foreground
                         hover:bg-muted/70 transition-colors"
            >
              <RefreshCw className="w-4 h-4" />
              <span className="text-sm">重试</span>
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full aspect-[3/4] glass-card rounded-2xl flex flex-col items-center justify-center gap-4 opacity-60">
      <div className="w-20 h-20 rounded-full bg-muted/50 flex items-center justify-center float">
        <Sparkles className="w-10 h-10 text-muted-foreground" />
      </div>
      <div className="text-center px-6">
        <p className="text-foreground font-medium">换装效果预览</p>
        <p className="text-sm text-muted-foreground mt-1">上传照片并选择服装后<br />点击生成按钮</p>
      </div>
    </div>
  );
}

import { ClothingCard } from './ClothingCard';

const CLOTHING_ITEMS = [
  {
