@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 250 20% 6%;
    --foreground: 0 0% 98%;
    --card: 250 15% 10%;
    --card-foreground: 0 0% 98%;
    --popover: 250 15% 10%;
    --popover-foreground: 0 0% 98%;
    --primary: 280 80% 65%;
    --primary-foreground: 0 0% 100%;
    --secondary: 320 70% 60%;
    --secondary-foreground: 0 0% 100%;
    --muted: 250 15% 15%;
    --muted-foreground: 250 10% 60%;
    --accent: 280 60% 50%;
    --accent-foreground: 0 0% 100%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 98%;
    --border: 250 15% 20%;
    --input: 250 15% 15%;
    --ring: 280 80% 65%;
    --radius: 0.75rem;

    /* Custom gradients */
    --gradient-primary: linear-gradient(135deg, hsl(280 80% 65%), hsl(320 70% 60%));
    --gradient-secondary: linear-gradient(135deg, hsl(320 70% 60%), hsl(350 80% 65%));
    --gradient-glass: linear-gradient(135deg, hsl(250 15% 15% / 0.8), hsl(250 15% 10% / 0.6));
    --gradient-shine: linear-gradient(135deg, hsl(280 80% 70% / 0.2), transparent);

    /* Shadows */
    --shadow-glow: 0 0 40px hsl(280 80% 65% / 0.3);
    --shadow-card: 0 8px 32px hsl(0 0% 0% / 0.3);
    --shadow-hover: 0 12px 40px hsl(280 80% 65% / 0.25);

    /* Animations */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-bounce: all 0.4s cubic-bezier(0.34, 1.56, 0.64, 1);
  }
}

@layer components {
  .glass-card {
    @apply bg-card/60 backdrop-blur-xl border border-border/50 rounded-2xl;
    box-shadow: var(--shadow-card);
  }

  .gradient-text {
    @apply bg-clip-text text-transparent;
    background-image: var(--gradient-primary);
  }

  .gradient-border {
    @apply relative;
  }

  .gradient-border::before {
    content: '';
    @apply absolute inset-0 rounded-2xl p-[1px];
    background: var(--gradient-primary);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
  }

  .btn-gradient {
    background: var(--gradient-primary);
    @apply text-primary-foreground font-semibold px-6 py-3 rounded-xl;
    box-shadow: var(--shadow-glow);
    transition: var(--transition-smooth);
  }

  .btn-gradient:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-hover);
  }

  .btn-gradient:disabled {
    @apply opacity-50 cursor-not-allowed;
    transform: none;
  }

  .upload-zone {
    @apply border-2 border-dashed border-border/50 rounded-2xl;
    @apply bg-muted/30 backdrop-blur-sm;
    @apply transition-all duration-300;
  }

  .upload-zone:hover {
    @apply border-primary/50 bg-muted/50;
  }

  .clothing-card {
    @apply glass-card overflow-hidden cursor-pointer;
    transition: var(--transition-bounce);
  }

  .clothing-card:hover {
    transform: translateY(-4px) scale(1.02);
    box-shadow: var(--shadow-hover);
  }

  .clothing-card.selected {
    @apply ring-2 ring-primary ring-offset-2 ring-offset-background;
  }

  .shimmer {
    background: linear-gradient(
      90deg,
      hsl(var(--muted)) 0%,
      hsl(var(--muted-foreground) / 0.1) 50%,
      hsl(var(--muted)) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  @keyframes shimmer {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }

  @keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 20px hsl(280 80% 65% / 0.3); }
    50% { box-shadow: 0 0 40px hsl(280 80% 65% / 0.6); }
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-10px); }
  }

  .float {
    animation: float 3s ease-in-out infinite;
  }

  @keyframes spin-slow {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }

  .spin-slow {
    animation: spin-slow 3s linear infinite;
  }
}

import { useCallback, useState } from 'react';
import { Upload, X, User } from 'lucide-react';

interface UploadZoneProps {
  image: string | null;
  onImageChange: (image: string | null) => void;
}

export function UploadZone({ image, onImageChange }: UploadZoneProps) {
  const [isDragging, setIsDragging] = useState(false);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const file = e.dataTransfer.files[0];
    if (file && file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onImageChange(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  }, [onImageChange]);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onImageChange(e.target?.result as string);
      };
      reader.readAsDataURL(file);
    }
  }, [onImageChange]);

  const handleClear = useCallback(() => {
    onImageChange(null);
  }, [onImageChange]);

  if (image) {
    return (
      <div className="relative w-full aspect-[3/4] rounded-2xl overflow-hidden glass-card group">
        <img
          src={image}
          alt="Uploaded"
          className="w-full h-full object-cover"
        />
        <button
          onClick={handleClear}
          className="absolute top-3 right-3 p-2 rounded-full bg-background/80 backdrop-blur-sm
                     text-foreground opacity-0 group-hover:opacity-100 transition-opacity
                     hover:bg-destructive hover:text-destructive-foreground"
        >
          <X className="w-4 h-4" />
        </button>
        <div className="absolute bottom-0 left-0 right-0 p-3 bg-gradient-to-t from-background/90 to-transparent">
          <p className="text-sm text-muted-foreground">点击右上角可更换照片</p>
        </div>
      </div>
    );
  }

  return (
    <div
      className={`upload-zone w-full aspect-[3/4] flex flex-col items-center justify-center gap-4 cursor-pointer
                  ${isDragging ? 'border-primary bg-primary/10' : ''}`}
      onDragOver={(e) => { e.preventDefault(); setIsDragging(true); }}
      onDragLeave={() => setIsDragging(false)}
      onDrop={handleDrop}
      onClick={() => document.getElementById('file-input')?.click()}
    >
      <input
        id="file-input"
        type="file"
        accept="image/*"
        className="hidden"
        onChange={handleFileInput}
      />
      <div className="w-20 h-20 rounded-full bg-muted flex items-center justify-center">
        <User className="w-10 h-10 text-muted-foreground" />
      </div>
      <div className="flex flex-col items-center gap-2">
        <div className="flex items-center gap-2 text-foreground">
          <Upload className="w-5 h-5" />
          <span className="font-medium">上传人物照片</span>
        </div>
        <p className="text-sm text-muted-foreground text-center px-4">
          拖放或点击上传<br />支持 JPG, PNG 格式
        </p>
      </div>
    </div>
  );
}

import { Check } from 'lucide-react';

interface ClothingCardProps {
  id: string;
  image: string;
  name: string;
  selected: boolean;
  onSelect: (id: string) => void;
}

export function ClothingCard({ id, image, name, selected, onSelect }: ClothingCardProps) {
  return (
    <div
      className={`clothing-card p-2 ${selected ? 'selected' : ''}`}
      onClick={() => onSelect(id)}
    >
      <div className="relative aspect-[3/4] rounded-xl overflow-hidden bg-muted">
        <img
          src={image}
          alt={name}
          className="w-full h-full object-cover"
        />
        {selected && (
          <div className="absolute top-2 right-2 w-6 h-6 rounded-full bg-primary flex items-center justify-center">
            <Check className="w-4 h-4 text-primary-foreground" />
          </div>
        )}
      </div>
      <p className="mt-2 text-sm text-foreground text-center truncate px-1">{name}</p>
    </div>
  );
}

import { Sparkles, Download, RefreshCw } from 'lucide-react';

interface ResultPreviewProps {
  result: string | null;
  isLoading: boolean;
  onDownload: () => void;
  onRetry: () => void;
}

export function ResultPreview({ result, isLoading, onDownload, onRetry }: ResultPreviewProps) {
  if (isLoading) {
    return (
      <div className="w-full aspect-[3/4] glass-card rounded-2xl flex flex-col items-center justify-center gap-4">
        <div className="relative">
          <div className="w-16 h-16 rounded-full border-4 border-muted border-t-primary spin-slow" />
          <Sparkles className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-6 h-6 text-primary" />
        </div>
        <div className="text-center">
          <p className="text-foreground font-medium">AI 正在换装中...</p>
          <p className="text-sm text-muted-foreground mt-1">请稍候，这可能需要几秒钟</p>
        </div>
      </div>
    );
  }

  if (result) {
    return (
      <div className="relative w-full aspect-[3/4] rounded-2xl overflow-hidden glass-card group">
        <img
          src={result}
          alt="AI Result"
          className="w-full h-full object-cover"
        />
        <div className="absolute bottom-0 left-0 right-0 p-4 bg-gradient-to-t from-background/90 to-transparent
                        opacity-0 group-hover:opacity-100 transition-opacity">
          <div className="flex gap-2">
            <button
              onClick={onDownload}
              className="flex-1 flex items-center justify-center gap-2 py-2 rounded-lg
                         bg-primary/20 backdrop-blur-sm text-primary-foreground
                         hover:bg-primary/30 transition-colors"
            >
              <Download className="w-4 h-4" />
              <span className="text-sm">下载</span>
            </button>
            <button
              onClick={onRetry}
              className="flex-1 flex items-center justify-center gap-2 py-2 rounded-lg
                         bg-muted/50 backdrop-blur-sm text-foreground
                         hover:bg-muted/70 transition-colors"
            >
              <RefreshCw className="w-4 h-4" />
              <span className="text-sm">重试</span>
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full aspect-[3/4] glass-card rounded-2xl flex flex-col items-center justify-center gap-4 opacity-60">
      <div className="w-20 h-20 rounded-full bg-muted/50 flex items-center justify-center float">
        <Sparkles className="w-10 h-10 text-muted-foreground" />
      </div>
      <div className="text-center px-6">
        <p className="text-foreground font-medium">换装效果预览</p>
        <p className="text-sm text-muted-foreground mt-1">上传照片并选择服装后<br />点击生成按钮</p>
      </div>
    </div>
  );
}

